    def browseSrcFile(self):
        filename = tkfd.askopenfilename(initialdir=self._convConfigHandler.readMusescoreScoresDefaultFldr(), filetypes=(("XML Files", "*.xml"),
                                                                                         ("All Files", "*.*")))
        self.endSrcFile.delete(0, END)
        self.endSrcFile.insert(0, filename)

    def browseDestFile(self):
        filename = tkfd.asksaveasfilename(initialdir=self._convConfigHandler.readPlaneShiftScoresDefaultFldr(), filetypes=(("XML Files", "*.xml"),
                                                                                             ("All Files", "*.*")))
        self.endDestFile.delete(0, END)
        self.endDestFile.insert(0, filename)

    # function used to set default musescore dir
    def setMusescoreScoresDefaultFldr(self):
        fldrPath = tkfd.askdirectory()
        self._convConfigHandler.writeMusescoreScoresDefaultFldr(fldrPath)
        self.entMCXMLSrcFldr.configure(state=NORMAL)
        self.entMCXMLSrcFldr.delete(0, END)
        self.entMCXMLSrcFldr.insert(0, fldrPath)
        self.entMCXMLSrcFldr.configure(state="readonly")

    # function used to set default musescore dir
    def setDefaultDestScoreDir(self):
        fldrPath = tkfd.askdirectory()
        self._convConfigHandler.writePlaneShiftScoresDefaultFldr(fldrPath)
        self.entPSSheetDestFldr.configure(state=NORMAL)
        self.entPSSheetDestFldr.delete(0, END)
        self.entPSSheetDestFldr.insert(0, fldrPath)
        self.entPSSheetDestFldr.configure(state="readonly")

    def convertXMLButtonAction(self):
        srcFilename = self.endSrcFile.get()
        destFilename = self.endDestFile.get()

        try:
            convXML = self._converter.convertXML(srcFilename)
        except RuntimeError as e:
            self.populateStatusMsgBox("Error occured while processing the input file. Exited with '{}'".format(e))
        # used for debug purposes
        # print(convXML)

        try:
            with open(destFilename, "wb") as text_file:
                text_file.write(convXML)
                self.populateStatusMsgBox("File written out successfully to '{}'".format(destFilename))
        except (OSError, IOError) as e:
            self.populateStatusMsgBox("Your file could not be written to, your exception details are: {}".format(e))

    def populateStatusMsgBox(self, msg):
        # set the response menssage
        self.statusMsg.configure(state=NORMAL)
        self.statusMsg.delete(1.0, END)
        self.statusMsg.insert(END, msg)
        self.statusMsg.configure(state=DISABLED)


